#!/bin/bash

# Localflix by @asyncze (Michael Sj√∂berg)
#
# This script generates an index.html gallery from all .jpg and .png files in the current directory.
# Hovering over an image shows its name with a transparent dark overlay.

shopt -s nullglob

# generate index page

index="_index.html"

cat << 'EOF' > "$index"
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">

<title>LOCALFLIX</title>

<style>
    body { 
        background-color: #141414; 
        color: #fff; 
        font-family: Arial, sans-serif; 
        margin: 0; 
        padding: 20px; 
    }
    h1 { 
        text-align: center;
        color: #E50914;
        font-weight: bold;
    }
    .logo {
        display: block;
        margin: 40px auto;
        max-width: 100%;
        border-bottom-left-radius: 20px;
        border-bottom-right-radius: 20px;
    }
    .gallery {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
        grid-gap: 20px;
        margin-top: 20px;
    }
    .gallery-item {
        border-radius: 24px;
        position: relative;
        overflow: hidden;
    }
    .gallery-item img {
        width: 100%;
        border-radius: 8px;
        transition: transform 0.3s ease;
        display: block;
    }
    .gallery-item:hover img {
        transform: scale(1.05);
    }
    .overlay {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(0, 0, 0, 0.5);
        color: white;
        opacity: 0;
        transition: opacity 0.3s ease;
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: 24px;
    }
    .gallery-item:hover .overlay {
        opacity: 1;
    }
    .text {
        font-size: 18px;
        font-weight: bold;
        text-align: center;
        padding: 5px;
    }
    .player-container {
        grid-column: 1 / -1;

        border-radius: 8px;
        overflow: hidden;
    }
    .player-container video {
        width: 100%;
        display: block;
    }
    .close-button {
        position: absolute;
        top: 10px;
        right: 10px;
        background-color: rgba(0, 0, 0, 0.5);
        color: #fff;
        border: none;
        font-size: 20px;
        cursor: pointer;
        padding: 5px 10px;
        border-radius: 4px;
        z-index: 9999 !important;
    }
</style>
</head>
<body>

<img src="data:image/png;base64," alt="Localflix Logo" class="logo">

<div class="gallery">
EOF

